import React, { useEffect, useState } from "react";
import MyImage from "./assets/images/saillogo.png"; // Your sailboat image

const Hero = () => {
  const [position, setPosition] = useState({ x: 0, y: 300 });
  const [touchStart, setTouchStart] = useState({ x: 0, y: 0 });
  const [isMobile, setIsMobile] = useState(false); // Track if it's mobile

  // Set isMobile based on window width
  const updateIsMobile = () => {
    if (window.innerWidth <= 768) {
      setIsMobile(true); // Mobile size
    } else {
      setIsMobile(false); // Desktop size
    }
  };

  useEffect(() => {
    updateIsMobile(); // Check on initial load

    // Check on window resize
    window.addEventListener("resize", updateIsMobile);
    return () => {
      window.removeEventListener("resize", updateIsMobile); // Clean up on unmount
    };
  }, []);

  // Handle keydown events for desktop (arrow keys)
  const handleKeyDown = (event) => {
    const step = 10;
    let newPosition = { ...position };

    switch (event.key) {
      case "ArrowUp":
        newPosition.y -= step;
        break;
      case "ArrowDown":
        newPosition.y += step;
        break;
      case "ArrowLeft":
        newPosition.x -= step;
        break;
      case "ArrowRight":
        newPosition.x += step;
        break;
      default:
        break;
    }

    setPosition(newPosition);
  };

  // Handle touch events for mobile
  const handleTouchStart = (e) => {
    const touchStartX = e.touches[0].clientX;
    const touchStartY = e.touches[0].clientY;
    setTouchStart({ x: touchStartX, y: touchStartY });
  };

  const handleTouchMove = (e) => {
    if (!touchStart.x || !touchStart.y) return; // Ignore if no touch start position

    const touchEndX = e.touches[0].clientX;
    const touchEndY = e.touches[0].clientY;

    const deltaX = touchEndX - touchStart.x;
    const deltaY = touchEndY - touchStart.y;

    let newPosition = { ...position };

    newPosition.x += deltaX / 10;
    newPosition.y += deltaY / 10;

    setPosition(newPosition);
    setTouchStart({ x: touchEndX, y: touchEndY }); // Update touch start for next movement
  };

  const handleTouchEnd = () => {
    setTouchStart({ x: 0, y: 0 });
  };

  // Attach and clean up keydown event listener for desktop
  useEffect(() => {
    if (!isMobile) {
      window.addEventListener("keydown", handleKeyDown);
    }

    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [isMobile, position]);

  // Attach and clean up touch event listeners for mobile
  useEffect(() => {
    if (isMobile) {
      window.addEventListener("touchstart", handleTouchStart);
      window.addEventListener("touchmove", handleTouchMove);
      window.addEventListener("touchend", handleTouchEnd);
    }

    return () => {
      window.removeEventListener("touchstart", handleTouchStart);
      window.removeEventListener("touchmove", handleTouchMove);
      window.removeEventListener("touchend", handleTouchEnd);
    };
  }, [isMobile, position, touchStart]);

  return (
    <div className="hero text-[#090704] p-0 m-0 h-screen w-full text-center flex flex-col justify-center items-center bg-[#f7f7f7] relative">
      {/* Title */}
      <h1 className="text-6xl font-bold text-[#090704] mt-12 mb-16 tracking-wide z-20">
        DAPHNE WU
      </h1>

      {/* Render Sailboat Image Only for Desktop */}
      {!isMobile && (
        <img
          src={MyImage}
          alt="Sail Logo"
          className="sailboat-image"
          style={{
            position: "absolute",
            left: `${position.x}px`,
            top: `${position.y}px`,
            width: "200px", // Default size for desktop
          }}
        />
      )}

      {/* Render a Smaller Sailboat for Mobile */}
      {isMobile && (
        <img
          src={MyImage}
          alt="Sail Logo"
          className="sailboat-image"
          style={{
            position: "absolute",
            left: `${position.x}px`,
            top: `${position.y}px`,
            width: "120px", // Smaller size for mobile
          }}
        />
      )}

      {/* Optional: Down Arrow */}
      <div
        className="down-arrow"
        style={{
          position: "absolute",
          bottom: "10px",
          left: "50%",
          transform: "translateX(-50%)",
          color: "#090704", // Customize the arrow color
          fontSize: "2rem", // Adjust the size of the arrow
        }}
      >
        ↓
      </div>
    </div>
  );
};

export default Hero;
