import React, { useState } from "react";
import emailjs from "emailjs-com"; // EmailJS service for sending emails
import { useNavigate } from "react-router-dom"; // To navigate back

const Contact = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [status, setStatus] = useState(""); // For showing success or error messages

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // EmailJS service
    emailjs
      .sendForm(
        "YOUR_SERVICE_ID", // Replace with your service ID
        "YOUR_TEMPLATE_ID", // Replace with your template ID
        e.target, // the form element
        "YOUR_USER_ID" // Replace with your user ID
      )
      .then(
        (response) => {
          setStatus("Message sent successfully!");
          setFormData({
            name: "",
            email: "",
            message: "",
          });
        },
        (error) => {
          setStatus("Failed to send message. Please try again.");
        }
      );
  };

  return (
    <div className="h-screen flex justify-center items-center bg-[#141414] text-white">
      <div className="w-full max-w-lg p-6 bg-[#1f1f1f] rounded-lg shadow-lg">
        <h2 className="text-3xl font-semibold text-center mb-4">Say Hi!</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block mb-2">Name</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="w-full px-4 py-2 bg-[#2a2a2a] text-white rounded-md"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block mb-2">Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="w-full px-4 py-2 bg-[#2a2a2a] text-white rounded-md"
              required
            />
          </div>
          <div className="mb-4">
            <label className="block mb-2">Message</label>
            <textarea
              name="message"
              value={formData.message}
              onChange={handleChange}
              rows="4"
              className="w-full px-4 py-2 bg-[#2a2a2a] text-white rounded-md"
              required
            />
          </div>
          <button
            type="submit"
            className="w-full py-2 bg-[#3c3c3c] text-white rounded-lg hover:bg-[#555] transition-colors duration-300"
          >
            Send
          </button>
        </form>
        {status && (
          <div className="mt-4 text-center">
            <p>{status}</p>
          </div>
        )}
        <div className="mt-4 text-center">
          <button
            onClick={() => navigate(-1)} // Navigate back to the previous page
            className="py-2 px-6 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition"
          >
            Go Back
          </button>
        </div>
      </div>
    </div>
  );
};

export default Contact;
