import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // For navigation back
import emailjs from "emailjs-com"; // EmailJS for sending the email
import './styles/Contact.css';

const Contact = () => {
  const [message, setMessage] = useState('');
  const [isSending, setIsSending] = useState(false);
  const [status, setStatus] = useState("");

  const handleInputChange = (e) => {
    setMessage(e.target.value);
  };

  const handleSend = (e) => {
    e.preventDefault();
    setIsSending(true);

    emailjs
      .send(
        "YOUR_SERVICE_ID",
        "YOUR_TEMPLATE_ID",
        { message },
        "YOUR_USER_ID"
      )
      .then(
        (response) => {
          setStatus("Message sent successfully!");
          setMessage("");
          setIsSending(false);
        },
        (error) => {
          setStatus("Failed to send message. Please try again.");
          setIsSending(false);
        }
      );
  };

  return (
    <div className="contact-page">
      <div className="message-box">
        <h1 className="contact-title">Let's Talk!</h1>
        <p className="contact-subtitle">
          Type your message and I'll get back to you soon. <br />
          Your message matters!
        </p>
        <textarea
          value={message}
          onChange={handleInputChange}
          placeholder="Write something..."
          className="message-input"
        />
        <button
          className={`send-btn ${isSending ? "sending" : ""}`}
          onClick={handleSend}
          disabled={isSending}
        >
          {isSending ? "Sending..." : "Send"}
        </button>
        {status && <p className={`status ${status.includes("success") ? "success" : "error"}`}>{status}</p>}
      </div>
    </div>
  );
};

export default Contact;
