import React, { useEffect, useState } from 'react';
import '/Users/daphnewu/Desktop/Projects/portfolio/src/components/styles/Cursor.css'; // Make sure to create the CSS file for styling

const Cursor = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isHoveringText, setIsHoveringText] = useState(false);

  useEffect(() => {
    // Track mouse position
    const updateMousePosition = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    // Add mousemove event listener
    document.addEventListener('mousemove', updateMousePosition);

    return () => {
      // Clean up the event listener
      document.removeEventListener('mousemove', updateMousePosition);
    };
  }, []);

  // Handle text hover effect (text will react to cursor)
  const handleMouseEnterText = () => {
    setIsHoveringText(true);
  };

  const handleMouseLeaveText = () => {
    setIsHoveringText(false);
  };

  return (
    <>
      {/* Custom Cursor */}
      <div
        className={`custom-cursor ${isHoveringText ? 'hovering-text' : ''}`}
        style={{
          left: `${mousePosition.x}px`,
          top: `${mousePosition.y}px`,
        }}
      ></div>

      {/* Example Text */}
      <div
        className="text-hover-example"
        onMouseEnter={handleMouseEnterText}
        onMouseLeave={handleMouseLeaveText}
      >
        <h1>Hover over me!</h1>
      </div>
    </>
  );
};

export default Cursor;
