import React, { useEffect, useState } from "react";
import MyImage from "./assets/images/saillogo.png"; // Adjust the path based on where the image is located

const Hero = () => {
  const [hasScrolled, setHasScrolled] = useState(false);

  // Function to handle scroll event
  const handleScroll = () => {
    // Check if the user has scrolled past a certain point (e.g., 100px)
    if (window.scrollY > 100) {
      setHasScrolled(true);  // Trigger animation after scrolling starts
    } else {
      setHasScrolled(false);
    }
  };

  // Set up the scroll event listener
  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);
    return (
        <div className="hero text-[#090704] p-10 text-center flex flex-col md:flex-row items-center md:items-start space-x-0 md:space-x-8 bg-[#f7f7f7]">
        {/* Image positioned on the left */}
        <img 
            src={MyImage} 
            alt="Sail Logo" 
            style={{ width: "35%", height: "auto" }} 
            className="md:order-first w-[35%] animate-swim" // Ensures the image stays left-aligned for larger screens
        />
        <div className="font-custom transition-transform hover:scale-105">
            <h1 className="text-4xl">Hey there!</h1>
            <p className="text-lg mt-4">I'm Daphne</p>
        </div>
    </div>
    );
};

export default Hero;
