import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // For navigation back
import emailjs from "emailjs-com"; // EmailJS for sending the email
import './styles/Contact.css';

const Contact = () => {
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState('');
  const [isSending, setIsSending] = useState(false);

  const handleInputChange = (e) => {
    setMessage(e.target.value);
  };

  const handleSend = (e) => {
    e.preventDefault();
    setIsSending(true);

    emailjs.send(
      'YOUR_SERVICE_ID',
      'YOUR_TEMPLATE_ID',
      { message },
      'YOUR_USER_ID'
    ).then(
      (response) => {
        setStatus('Message sent successfully!');
        setMessage('');
        setIsSending(false);
      },
      (error) => {
        setStatus('Failed to send message. Please try again.');
        setIsSending(false);
      }
    );
  };

  return (
    <div className="contact-page">
      <div className="message-box">
        <h1 className="contact-title">Hey! Letâ€™s Chat.</h1>
        <p className="contact-subtitle">
          Type away and I'll get back to you soon. Your message matters!
        </p>

        <textarea
          value={message}
          onChange={handleInputChange}
          placeholder="Type your message here..."
          className="message-input"
        />

        <button
          className={`send-btn ${isSending ? 'sending' : ''}`}
          onClick={handleSend}
          disabled={isSending}
        >
          {isSending ? 'Sending...' : 'Send'}
        </button>

        {status && <p className={`status ${status.includes('success') ? 'success' : 'error'}`}>{status}</p>}
      </div>
    </div>
  );
};

export default Contact;
