import React, { useEffect, useState } from "react";
import MyImage from "./assets/images/saillogo.png"; // Adjust the path based on where the image is located

const Hero = () => {
  const [offset, setOffset] = useState(0); // Simulated scroll offset

  useEffect(() => {
    let animationFrameId;

    // Function to increment the offset for animation
    const animate = () => {
      setOffset((prev) => prev + 0.05); // Increment offset for smooth animation
      animationFrameId = requestAnimationFrame(animate); // Keep animating
    };

    // Start animation loop
    animate();

    // Cleanup on component unmount
    return () => cancelAnimationFrame(animationFrameId);
  }, []);

  // Calculate swim and bob positions
  const swimPosition = Math.sin(offset) * 50; // Oscillating horizontal movement
  const bobPosition = Math.sin(offset) * 20; // Vertical bobbing

  return (
    <div className="hero text-[#090704] p-0 m-0 h-screen w-full text-center flex flex-col justify-center items-center bg-[#f7f7f7] overflow-hidden">
      {/* Title */}
      <h1 className="text-6xl font-bold text-[#090704] mt-12 mb-16 tracking-wide">
        DAPHNE WU
      </h1>

      {/* Image positioned on the left */}
      <img
        src={MyImage}
        alt="Sail Logo"
        style={{
          transform: `translateX(${swimPosition}px) translateY(${bobPosition}px)`, // Apply animation-driven movement
        }}
        className="w-[500px] h-auto"
      />
    </div>
  );
};

export default Hero;
