import React, { useEffect, useState } from "react";
import MyImage from "./assets/images/saillogo.png"; // Adjust the path based on where the image is located

const Hero = () => {
  const [scrollPosition, setScrollPosition] = useState(0);

  // Function to handle scroll event
  const handleScroll = () => {
    setScrollPosition(window.scrollY); // Track scroll position
  };

  // Set up the scroll event listener
  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // Horizontal movement speed and bobbing effect based on scroll position
  const swimPosition = Math.sin(scrollPosition * 0.05) * 50; // Oscillating horizontal movement
  const bobPosition = Math.sin(scrollPosition * 0.05) * 30; // Vertical bobbing

  return (
    <div className="hero text-[#090704] p-0 m-0 h-screen w-full text-center flex flex-col justify-center items-center bg-[#f7f7f7] overflow-hidden">
      {/* Title */}
      <h1 className="text-6xl font-bold text-[#090704] mt-12 mb-16 tracking-wide">
        DAPHNE WU
      </h1>

      {/* Image positioned on the left */}
      <img
        src={MyImage}
        alt="Sail Logo"
        style={{
          transform: `translateX(${swimPosition}px) translateY(${bobPosition}px)`, // Apply scroll-driven movement
          transition: "transform 0.1s ease-out", // Smooth transition
        }}
        className="w-[500px] h-auto transition-transform duration-1000"
      />

      {/* Subtext */}
      {/* <div className="font-custom transition-transform hover:scale-105 mt-4">
        <h2 className="text-4xl">Hey there!</h2>
        <p className="text-lg mt-4">I'm Daphne</p>
      </div> */}
    </div>
  );
};

export default Hero;
