import React, { useEffect, useState, useRef } from "react";
import MyImage from "./assets/images/saillogo.png";

const Hero = () => {
  const imageWidth = 700;
  const [position, setPosition] = useState(-imageWidth); // Start off-screen to the left
  const [isVisible, setIsVisible] = useState(false); // Track visibility of the section
  const heroRef = useRef(null); // Reference to the Hero section

  useEffect(() => {
    let animationFrameId;

    // Function to animate the image across the screen
    const animate = () => {
      setPosition((prev) =>
        prev >= window.innerWidth ? -imageWidth : prev + 2 // Reset to the left after leaving the screen
      );
      animationFrameId = requestAnimationFrame(animate);
    };

    animate();

    return () => cancelAnimationFrame(animationFrameId);
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      if (heroRef.current) {
        const rect = heroRef.current.getBoundingClientRect();

        // Debugging information
        console.log("Section top:", rect.top);
        console.log("Section bottom:", rect.bottom);
        console.log("Window height:", window.innerHeight);

        // Check if the top of the section is in the viewport
        const isInView =
          rect.top >= 0 && rect.top <= window.innerHeight; // Check if the top of the section is in view

        setIsVisible(isInView);
      }
    };

    window.addEventListener("scroll", handleScroll);

    // Initial check
    handleScroll();

    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const bobPosition = Math.sin(position * 0.05) * 20; // Vertical bobbing

  return (
    <div
      ref={heroRef}
      className="hero text-[#090704] p-0 m-0 w-full text-center flex flex-col justify-center items-center bg-[#f7f7f7] overflow-hidden"
      style={{ minHeight: '100vh' }} // Ensure the container takes at least the full height of the viewport
    >
      {/* Title */}
      <h1 className="text-6xl font-bold text-[#090704] mt-12 mb-16 tracking-wide">
        DAPHNE WU
      </h1>

      {/* Conditionally render the image */}
      {isVisible && (
        <img
          src={MyImage}
          alt="Sail Logo"
          style={{
            transform: `translateX(${position}px) translateY(${bobPosition}px)`,
            position: "absolute",
          }}
          className="w-[700px] h-auto"
        />
      )}
    </div>
  );
};

export default Hero;
