import React from "react";

const HeroSection = () => {
  return (
    <div className="bg-black text-white min-h-screen flex flex-col justify-center items-center space-y-6 px-8">
      {/* Intro */}
      <div className="text-center">
        <h1 className="text-6xl sm:text-8xl font-bold leading-tight">
          hi. <br /> i’m <span className="text-gradient bg-gradient-to-r from-pink-500 to-blue-500">adrian.</span>
        </h1>
        <p className="text-lg sm:text-xl text-gray-400 mt-4">
          I’m a designer working on the Human Interface Team at Apple. My passion is to create emotional experiences at the intersection of art, design, and AI.
        </p>
      </div>

      {/* Button */}
      <button className="mt-6 px-6 py-3 border border-white text-white rounded-full hover:bg-white hover:text-black transition">
        → say hi
      </button>

      {/* Spacer (optional if you want extra space between hero and projects) */}
      <div className="mt-16"></div>
    </div>
  );
};

export default HeroSection;





