import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Navbar from "./components/Navbar";
import Hero from "./components/Hero";
import Projects from "./components/Projects";
import ProjectRacc from "./components/projects/ProjectRacc";
import Skills from "./components/Skills";
import Resume from "./components/Resume";
import Cursor from "./components/Cursor"; // Import the custom cursor
import './App.css';
import './index.css';

const App = () => {
  const [showNavbar, setShowNavbar] = useState(true); // Navbar is shown by default

  useEffect(() => {
    // Set navbar visibility to true when at the root path
    if (window.location.pathname === '/') {
      setShowNavbar(true);
    } else {
      setShowNavbar(false);
    }
  }, [window.location.pathname]);  // Run on path change

  return (
    <Router>
      <div className="overflow-y-auto h-screen">
        {/* Navbar is visible on all pages, or based on conditions */}
        {showNavbar && <Navbar />}

        {/* Include the custom cursor */}
        <Cursor />

        <Routes>
          <Route
            path="/"
            element={
              <>
                <div id="hero">
                  {/* Pass setShowNavbar to Hero */}
                  <Hero setShowNavbar={setShowNavbar} />
                </div>
                <div id="projects">
                  <Projects />
                </div>
              </>
            }
          />
          <Route path="/hero" element={<Hero setShowNavbar={setShowNavbar} />} />
          <Route path="/projects/racctracc" element={<ProjectRacc />} />
          <Route path="/skills" element={<Skills />} />
          <Route path="/resume" element={<Resume />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
