import React, { useEffect, useState } from "react";
import MyImage from "./assets/images/saillogo.png"; // Your project image

const Hero = () => {
  // State to track the position of the stick figure and projects
  const [stickPosition, setStickPosition] = useState({ x: 0, y: 300 });
  const [projectPosition, setProjectPosition] = useState({ x: 0, y: 300 });

  // Handle keydown events for arrow keys
  const handleKeyDown = (event) => {
    const step = 10; // Distance moved per key press
    let newStickPosition = { ...stickPosition };
    let newProjectPosition = { ...projectPosition };

    switch (event.key) {
      case "ArrowUp":
        newStickPosition.y -= step;
        newProjectPosition.y -= step;
        break;
      case "ArrowDown":
        newStickPosition.y += step;
        newProjectPosition.y += step;
        break;
      case "ArrowLeft":
        newStickPosition.x -= step;
        newProjectPosition.x -= step;
        break;
      case "ArrowRight":
        newStickPosition.x += step;
        newProjectPosition.x += step;
        break;
      default:
        break;
    }

    setStickPosition(newStickPosition);
    setProjectPosition(newProjectPosition);
  };

  // Attach and clean up keydown event listener
  useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);

    // Cleanup when component unmounts
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [stickPosition, projectPosition]); // Only re-run when position changes

  return (
    <div className="hero text-[#090704] p-0 m-0 h-screen w-full text-center flex flex-col justify-center items-center bg-[#f7f7f7] relative">
      {/* Title */}
      <h1 className="text-6xl font-bold text-[#090704] mt-12 mb-16 tracking-wide z-10">
        DAPHNE WU
      </h1>

      {/* Stick Figure (Simple CSS representation) */}
      <div
        className="stick-figure"
        style={{
          position: "absolute",
          left: `${stickPosition.x}px`,
          top: `${stickPosition.y}px`,
          width: "50px",
          height: "50px",
          backgroundColor: "black",
          borderRadius: "50%",
          zIndex: 1, // Ensure it's above other content
        }}
      >
        {/* You can enhance this with more CSS to create a stick figure */}
      </div>

      {/* Your Project (e.g., image) */}
      <img
        src={MyImage}
        alt="Sail Logo"
        style={{
          position: "absolute",
          left: `${projectPosition.x}px`,
          top: `${projectPosition.y}px`,
          width: "200px", // Adjust as per your requirement
          height: "auto",
          zIndex: 0, // Ensure it stays behind the stick figure
        }}
      />

      {/* Optional: Down Arrow */}
      <div
        className="down-arrow"
        style={{
          position: "absolute",
          bottom: "10px",
          left: "50%",
          transform: "translateX(-50%)",
          color: "#090704", // Customize the arrow color
          fontSize: "2rem", // Adjust the size of the arrow
        }}
      >
        â†“
      </div>
    </div>
  );
};

export default Hero;
