import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // For navigation back
import emailjs from "emailjs-com"; // EmailJS for sending the email
import './styles/Contact.css';

const Contact = () => {
  const navigate = useNavigate();
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState('');
  const [isSending, setIsSending] = useState(false);

  // Handle text input changes
  const handleInputChange = (e) => {
    setMessage(e.target.value);
  };

  // Handle the "Send" button click
  const handleSend = (e) => {
    e.preventDefault();
    setIsSending(true);

    emailjs.send(
      'YOUR_SERVICE_ID',
      'YOUR_TEMPLATE_ID',
      { message },
      'YOUR_USER_ID'
    )
    .then(
      (response) => {
        setStatus('Message sent successfully!');
        setMessage('');
        setIsSending(false); // End sending state
      },
      (error) => {
        setStatus('Failed to send message. Please try again.');
        setIsSending(false);
      }
    );
  };

  return (
    <div className="contact-page">
      <div className="container">
        <h2 className="title">Incoming!</h2>
        <p className="subtitle">Type your message below, Iâ€™m listening...</p>

        <div className="message-container">
          <input
            type="text"
            value={message}
            onChange={handleInputChange}
            placeholder="Type something cool..."
            className="message-input"
          />

          <button
            className={`send-btn ${isSending ? 'sending' : ''}`}
            onClick={handleSend}
            disabled={isSending}
          >
            {isSending ? 'Sending...' : 'Send'}
          </button>

          {status && <p className={`status ${status.includes('success') ? 'success' : 'error'}`}>{status}</p>}
        </div>

        <button onClick={() => navigate(-1)} className="back-btn">Go Back</button>
      </div>
    </div>
  );
};

export default Contact;
