import React, { useState } from "react";
import emailjs from "emailjs-com";
import { useNavigate } from "react-router-dom";
import './styles/Contact.css';

const Contact = () => {
  const [message, setMessage] = useState('');
  const [isSending, setIsSending] = useState(false);
  const [status, setStatus] = useState("");
  const navigate = useNavigate(); // Hook to navigate

  const handleInputChange = (e) => {
    setMessage(e.target.value);
  };

  const handleSend = (e) => {
    e.preventDefault();
    setIsSending(true);

    emailjs
      .send(
        "YOUR_SERVICE_ID",
        "YOUR_TEMPLATE_ID",
        { message },
        "YOUR_USER_ID"
      )
      .then(
        (response) => {
          setStatus("Message sent successfully!");
          setMessage("");
          setIsSending(false);
        },
        (error) => {
          setStatus("Failed to send message. Please try again.");
          setIsSending(false);
        }
      );
  };

  // Function to handle going back to the previous page
  const handleBack = () => {
    navigate("/work"); // Adjust the path to wherever you want the user to go
  };

  return (
    <div className="contact-page">
      <div className="message-box">
        <textarea
          value={message}
          onChange={handleInputChange}
          placeholder="Write something..."
          className="message-input"
        />
        <div className="button-container">
          <button
            className={`send-btn ${isSending ? "sending" : ""}`}
            onClick={handleSend}
            disabled={isSending}
          >
            {isSending ? "Sending..." : "Send"}
          </button>

          {/* Back Button */}
          className="mt-12 px-6 py-2 border border-white text-white rounded-full hover:bg-[#3c3c3c] hover:border-transparent hover:text-white transition-colors duration-300"

          <button className="back-btn" onClick={handleBack}>
            ‚Üê Back to Work
          </button>
        </div>
        {status && <p className={`status ${status.includes("success") ? "success" : "error"}`}>{status}</p>}
      </div>
    </div>
  );
};

export default Contact;
