import React from "react";
import MyImage from "./assets/images/daphneheadshot.png"; // Adjust the path based on where the image is located

const Hero = () => (
    <div className="hero text-[#090704] p-10 text-center flex flex-col md:flex-row items-center md:items-start space-x-0 md:space-x-8 bg-[#ffffffdb]">
        {/* Image positioned on the left */}
        <img 
            src={MyImage} 
            alt="Headshot of Daphne" 
            style={{ width: "35%", height: "auto" }} 
            className="md:order-first" // Ensures the image stays left-aligned for larger screens
        />
        <div className="font-custom transition-transform hover:scale-105">
            <h1 className="text-4xl">Hey there!</h1>
            <p className="text-lg mt-4">I'm Daphne</p>
        </div>
    </div>
);

export default Hero;
