import React, { useEffect, useState, useCallback } from "react";
import MyImage from "./assets/images/saillogo.png"; // Adjust path as necessary

const Hero = () => {
  const [position, setPosition] = useState({ x: 0, y: 300 });
  const [touchStart, setTouchStart] = useState({ x: 0, y: 0 });
  const [isMobile, setIsMobile] = useState(false);
  const [isMoving, setIsMoving] = useState(false);

  // Handle keydown events for arrow keys (Desktop)
  const handleKeyDown = useCallback((event) => {
    const step = 10;
    let newPosition = { ...position };

    switch (event.key) {
      case "ArrowUp":
        newPosition.y -= step;
        break;
      case "ArrowDown":
        newPosition.y += step;
        break;
      case "ArrowLeft":
        newPosition.x -= step;
        break;
      case "ArrowRight":
        newPosition.x += step;
        break;
      default:
        break;
    }

    setPosition(newPosition);
    setIsMoving(true);
  }, [position]);

  // Handle touch events for mobile (swiping)
  const handleTouchStart = useCallback((e) => {
    const touchStartX = e.touches[0].clientX;
    const touchStartY = e.touches[0].clientY;
    setTouchStart({ x: touchStartX, y: touchStartY });
  }, []);

  const handleTouchMove = useCallback((e) => {
    if (!touchStart.x || !touchStart.y) return;

    const touchEndX = e.touches[0].clientX;
    const touchEndY = e.touches[0].clientY;

    const deltaX = touchEndX - touchStart.x;
    const deltaY = touchEndY - touchStart.y;

    let newPosition = { ...position };

    newPosition.x += deltaX / 10;
    newPosition.y += deltaY / 10;

    setPosition(newPosition);
    setTouchStart({ x: touchEndX, y: touchEndY });
    setIsMoving(true);
  }, [touchStart, position]);

  const handleTouchEnd = useCallback(() => {
    setTouchStart({ x: 0, y: 0 });
  }, []);

  // Attach and clean up keydown event listener
  useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);

    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [handleKeyDown]);

  // Attach and clean up touch event listeners for mobile
  useEffect(() => {
    window.addEventListener("touchstart", handleTouchStart);
    window.addEventListener("touchmove", handleTouchMove);
    window.addEventListener("touchend", handleTouchEnd);

    return () => {
      window.removeEventListener("touchstart", handleTouchStart);
      window.removeEventListener("touchmove", handleTouchMove);
      window.removeEventListener("touchend", handleTouchEnd);
    };
  }, [handleTouchStart, handleTouchMove, handleTouchEnd]);

  return (
    <div className="hero text-[#090704] p-0 m-0 h-screen w-full text-center flex flex-col justify-center items-center bg-[#f7f7f7] relative">
      <h1 className="text-6xl font-bold text-[#090704] mt-12 mb-16 tracking-wide z-20">
        DAPHNE WU
      </h1>

      <div className="sailboat-container" style={{ position: "relative", width: "100%", height: "100%" }}>
        {/* Instruction Messages */}
        <p
          className="instruction-message text-[#090704] font-medium text-lg z-20"
          style={{
            position: "absolute",
            top: "50%",
            left: "100%",
            transform: "translateY(-50%)",
            padding: "10px",
            backgroundColor: "#f7f7f7",
            borderRadius: "5px",
            visibility: (isMobile && !isMoving) ? "visible" : "hidden",
          }}
        >
          Drag the sailboat image to move it
        </p>

        <p
          className="instruction-message text-[#090704] font-medium text-lg z-20"
          style={{
            position: "absolute",
            top: "50%",
            right: "100%",
            transform: "translateY(-50%)",
            padding: "10px",
            backgroundColor: "#f7f7f7",
            borderRadius: "5px",
            visibility: (!isMobile && !isMoving) ? "visible" : "hidden",
          }}
        >
          Use the arrow keys to move the sailboat
        </p>

        {/* Sailboat Image */}
        <img
          src={MyImage}
          alt="Sailboat"
          style={{
            position: "absolute",
            left: `${position.x}px`,
            top: `${position.y}px`,
            width: "200px", // Adjust this size if needed
            height: "auto",
            zIndex: 10,
            transition: "all 0.1s ease", // Add smooth transition for movement
            visibility: isMoving ? "visible" : "hidden", // Ensure image is visible when moved
          }}
        />
      </div>

      {/* Down Arrow */}
      <div
        className="down-arrow"
        style={{
          position: "absolute",
          bottom: "10px",
          left: "50%",
          transform: "translateX(-50%)",
          color: "#090704",
          fontSize: "2rem",
        }}
      >
        ↓
      </div>
    </div>
  );
};

export default Hero;
