import React, { useEffect, useState } from "react";
import MyImage from "./assets/images/saillogo.png"; // Adjust the path based on where the image is located

const Hero = () => {
  const [scrollPosition, setScrollPosition] = useState(0);

  // Function to handle scroll event
  const handleScroll = () => {
    setScrollPosition(window.scrollY);  // Track scroll position
  };

  // Set up the scroll event listener
  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // Calculate horizontal movement based on scroll position
  const swimPosition = scrollPosition * 0.1;  // Horizontal movement speed

  // Bob effect: Use Math.sin to create a bobbing effect with a noticeable amplitude
  const bobPosition = Math.sin(scrollPosition * 0.05) * 20;  // Vertical movement (bobbing effect)

  // Ensure the image stays within bounds and smoothly transition
  const style = {
    transform: `translateX(${swimPosition}px) translateY(${bobPosition}px)`,
    transition: "transform 0.1s ease-out", // Smooth transition for the transform
  };

  return (
    <div className="hero text-[#090704] p-10 text-center flex flex-col md:flex-row items-center md:items-start space-x-0 md:space-x-8 bg-[#f7f7f7]">
      {/* Image positioned on the left */}
      <img
        src={MyImage}
        alt="Sail Logo"
        style={style}  // Apply dynamic styles directly here
        className="w-[35%] h-auto"
      />
      <div className="font-custom transition-transform hover:scale-105">
        <h1 className="text-4xl">Hey there!</h1>
        <p className="text-lg mt-4">I'm Daphne</p>
      </div>
    </div>
  );
};

export default Hero;
