import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, useLocation } from "react-router-dom";
import Navbar from "./components/Navbar";
import Hero from "./components/Hero";
import Projects from "./components/Projects";
import ProjectRacc from "./components/projects/ProjectRacc";
import Skills from "./components/Skills";
import Resume from "./components/Resume";
import Cursor from "./components/Cursor"; // Import the custom cursor
import './App.css';
import './index.css';

const App = () => {
  const [showNavbar, setShowNavbar] = useState(false);
  const location = useLocation(); // Access the current location

  // Scroll to the top of the page whenever the route changes
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [location]); // This hook will trigger every time the route changes

  return (
    <div className="overflow-y-auto h-screen">
      {/* Conditionally render Navbar */}
      {showNavbar && <Navbar />}

      {/* Include the custom cursor */}
      <Cursor /> 

      <Routes>
        <Route
          path="/hero"
          element={
            <>
              <div id="hero">
                {/* Pass setShowNavbar to Hero */}
                <Hero setShowNavbar={setShowNavbar} />
              </div>
              <div id="projects">
                <Projects />
              </div>
            </>
          }
        />
        <Route path="/projects/racctracc" element={<ProjectRacc />} />
        <Route path="/skills" element={<Skills />} />
        <Route path="/resume" element={<Resume />} />
      </Routes>
    </div>
  );
};

export default App;
