import React, { useState, useEffect } from "react";
import "./AboutMe.css";

const AboutMe = () => {
  const [displayedText, setDisplayedText] = useState("");
  const fullText = "Hi, I'm Daphne! I'm a designer and developer creating beautiful, functional digital experiences. Let's connect!";

  // Typing effect for introduction
  useEffect(() => {
    const interval = setInterval(() => {
      if (displayedText.length < fullText.length) {
        setDisplayedText((prev) => prev + fullText[displayedText.length]);
      } else {
        clearInterval(interval);
      }
    }, 100);

    return () => clearInterval(interval); // Clean up the interval on unmount
  }, [displayedText]);

  const [showMore, setShowMore] = useState(false);
  const toggleShowMore = () => setShowMore(!showMore);

  return (
    <div className="aboutme-container">
      {/* Introduction Section */}
      <section className="intro">
        <h1>{displayedText}<span className="cursor">|</span></h1>
      </section>

      {/* Skills Section */}
      <section className="skills">
        <h2>My Skills</h2>
        <div className="skill-bars">
          <div className="skill-bar">
            <p>UX Design</p>
            <div className="progress ux"></div>
          </div>
          <div className="skill-bar">
            <p>UI Design</p>
            <div className="progress ui"></div>
          </div>
          <div className="skill-bar">
            <p>Web Development</p>
            <div className="progress web"></div>
          </div>
        </div>
      </section>

      {/* Fun Facts Section */}
      <section className="fun-facts">
        <button className="show-more-btn" onClick={toggleShowMore}>
          {showMore ? "Show Less" : "Show More"}
        </button>
        {showMore && (
          <div className="extra-info">
            <p>I love photography and am always on the lookout for new places to explore.</p>
            <p>In my spare time, I like experimenting with new design trends and technologies.</p>
          </div>
        )}
      </section>

      {/* Hoverable Photo Section */}
      <section className="photo-section">
        <h2>Here's Me!</h2>
        <div className="photo-container">
          <img src="your-photo.jpg" alt="Your Profile" className="photo" />
        </div>
      </section>
    </div>
  );
};

export default AboutMe;
