import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // For navigation back
import emailjs from "emailjs-com"; // EmailJS for sending the email
import './styles/Contact.css';

const Contact = () => {
  const navigate = useNavigate();
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState('');

  // Handle text input changes
  const handleInputChange = (e) => {
    setMessage(e.target.value);
  };

  // Handle the "Send" button click
  const handleSend = (e) => {
    e.preventDefault();

    // Send the email using EmailJS
    emailjs.send(
      'YOUR_SERVICE_ID',
      'YOUR_TEMPLATE_ID',
      { message },
      'YOUR_USER_ID'
    )
    .then(
      (response) => {
        setStatus('Message sent successfully!');
        setMessage(''); // Clear the input after sending
      },
      (error) => {
        setStatus('Failed to send message. Please try again.');
      }
    );
  };

  return (
    <div className="contact-page">
      <div className="container">
        <h2 className="title">Incoming!</h2>
        <p className="subtitle">Iâ€™m listening, type something below.</p>

        <div className="message-container">
          <input
            type="text"
            value={message}
            onChange={handleInputChange}
            placeholder="Type your message here..."
            className="message-input"
          />

          <button className="send-btn" onClick={handleSend}>
            Send
          </button>

          {status && <p className="status">{status}</p>}
        </div>

        <button onClick={() => navigate(-1)} className="back-btn">
          Go Back
        </button>
      </div>
    </div>
  );
};

export default Contact;
